void SPG2_Get3Weights(MATH_tdst_Matrix *pGOMatrix, MATH_tdst_Vector *P0,MATH_tdst_Vector *P1,MATH_tdst_Vector *P2,MATH_tdst_Vector *CP,MATH_tdst_Vector *PDST)
{
	MATH_tdst_Vector Edge0,Edge1;
	MATH_tdst_Vector L0,L1,L2,LCP;
	f32				 f0,f1,f2;

	LCP = *CP;

	MATH_TransformVertex(&L0 , pGOMatrix , P0);
	MATH_TransformVertex(&L1 , pGOMatrix , P1);
	MATH_TransformVertex(&L2 , pGOMatrix , P2);
	LCP.z = L0.z = L1.z = L2.z = 0.0f;

	MATH_SubVector(&Edge0 , &L1 , &L0);
	MATH_SubVector(&Edge1 , &L2 , &L1);
	/* Turn 90 */
	Edge0.z = Edge0.x;
	Edge0.x =-Edge0.y;
	Edge0.y = Edge0.z;
	Edge0.z = 0.0f;

	Edge1.z = Edge1.x;
	Edge1.x =-Edge1.y;
	Edge1.y = Edge1.z;
	Edge1.z = 0.0f;

	f0 = MATH_f_DotProduct(&Edge0 , &L1);
	f1 = MATH_f_DotProduct(&Edge0 , &L2);
	f2 = MATH_f_DotProduct(&Edge0 , &LCP);

#if 0 && defined (JADEFUSION)// BUG : to be investigated : defined(_XENON)
	float fVal = f1 - f0;
	if(*(int*)&fVal == 0)
		PDST->z = 0.0f;
	else
		PDST->z = fMax(0.0f, (f2 - f0) / (f1 - f0));
#else
	if ((f1 - f0) == 0.0f)
		PDST->z = 0.0f;
	else
		PDST->z = fMax(0.0f , (f2 - f0) / (f1 - f0));
#endif

	f0 = MATH_f_DotProduct(&Edge1 , &L1);
	f1 = MATH_f_DotProduct(&Edge1 , &L0);
	f2 = MATH_f_DotProduct(&Edge1 , &LCP);

#if 0 && defined (JADEFUSION)// BUG : to be investigated : defined(_XENON)
	fVal = f1 - f0;
	if(*(int*)&fVal == 0)
		PDST->x = 0.0f;
	else
	PDST->x = fMax(0.0f, (f2 - f0) / (f1 - f0));
#else
	if ((f1 - f0) == 0.0f)
		PDST->x = 0.0f;
	else
		PDST->x = fMax(0.0f , (f2 - f0) / (f1 - f0));
#endif

#if defined(_XENON)
	PDST->y = fMax(0.0f , 1.0f - (PDST->x + PDST->z));
#else
	PDST->y = fMax(0.0f , 1.0f - (PDST->x + PDST->z));
#endif
}

BOOL SPG2_InBV(float fx, float fy, MATH_tdst_Vector *min, MATH_tdst_Vector *max)
{
	if(fx < min->x) return FALSE;
	if(fx > max->x) return FALSE;
	if(fy < min->y) return FALSE;
	if(fy > max->y) return FALSE;
	return TRUE;
}

void SPG2_AddBV(float fx, float fy, MATH_tdst_Vector *min, MATH_tdst_Vector *max)
{
	if(fx < min->x) {min->x = fx;};
	if(fx > max->x) {max->x = fx;};
	if(fy < min->y) {min->y = fy;};
	if(fy > max->y) {max->y = fy;};
}

