void SPG2_Modifier_Create(OBJ_tdst_GameObject *_pst_GO, MDF_tdst_Modifier *_pst_Mod, void *p_Data)
{
	/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
	SPG2_tdst_Modifier *pst_Data;
	/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

	_pst_Mod->p_Data = MEM_p_AllocAlign(sizeof(SPG2_tdst_Modifier),16);
	
	pst_Data = (SPG2_tdst_Modifier *) _pst_Mod->p_Data;
	L_memset(pst_Data , 0 , sizeof(SPG2_tdst_Modifier));

	if(!p_Data)
	{
		SPG2_InitModifier(pst_Data);
	}
	else
	{
		MODIFIER_SPG2_FlushModifier((SPG2_tdst_Modifier *)p_Data);
		L_memcpy(_pst_Mod->p_Data, p_Data, sizeof(SPG2_tdst_Modifier));
	}
	/* Add modifier in global Ring */
	pst_Data->p_NextSPG2 = pst_FirstSPG2Modifier;
	pst_FirstSPG2Modifier = pst_Data;
	
#ifdef SPG2_DYNAMIC_CACHELINE_NB
	SPG2_iModifierNb++;
#endif // SPG2_DYNAMIC_CACHELINE_NB
}

/*
 =======================================================================================================================
 =======================================================================================================================
 */
void SPG2_Modifier_Destroy(MDF_tdst_Modifier *_pst_Mod)
{
	/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
	SPG2_tdst_Modifier *pst_Data;
	/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

	pst_Data = (SPG2_tdst_Modifier *) _pst_Mod->p_Data;
	MODIFIER_SPG2_FlushModifier(pst_Data);

	/* Delete modifier in global Ring */
	{
		SPG2_tdst_Modifier *p_Finder;
		p_Finder = pst_FirstSPG2Modifier;
		if (pst_Data == p_Finder) 
			pst_FirstSPG2Modifier = pst_FirstSPG2Modifier->p_NextSPG2;
		else
		{
			while (p_Finder->p_NextSPG2 != pst_Data)
				p_Finder = p_Finder->p_NextSPG2;
			p_Finder ->p_NextSPG2 = p_Finder ->p_NextSPG2 ->p_NextSPG2;
		}
	}

	MEM_FreeAlign(pst_Data);
	
#ifdef SPG2_DYNAMIC_CACHELINE_NB
	SPG2_iModifierNb--;
	if (0 == SPG2_iModifierNb)
		SPG2_DestroyCachedData();
#endif // SPG2_DYNAMIC_CACHELINE_NB
}
