float	MATH_ComputeCulling(
			MATH_tdst_Vector    *pst_CamPos , 
			MATH_tdst_Vector    *pst_CamDir , 
			float				CamCos , 
			float				CamOoSin , 
			MATH_tdst_Vector    *pst_SphereCenter , 
			float SphereRadius)
{
	float Lod ,Distance;
	MATH_tdst_Vector ProjectedCenter;
	MATH_tdst_Vector pCS;
	/* Fast Compute LOD (not the real formulae, but... OK)*/ 
	MATH_SubVector(&pCS , pst_SphereCenter , pst_CamPos );
	/* ZCulling 50 % of world */
	if (MATH_f_DotProduct(pst_CamDir , &pCS ) < -SphereRadius) 
		return 0.0f;
	/* into sphere ... Just use distance */
	Distance = MATH_f_NormVector(&pCS);
	if (Distance < SphereRadius) 
		return 1.0f;
	
	if (MATH_f_DotProduct(pst_CamDir , &pCS ) < 0.0f) 
		return 0.0f;
	
	Lod = 2.0f * (CamCos * CamOoSin * SphereRadius) / (2.0f * Distance);
	if (Lod > 1.0f) Lod = 1.0f;
	
	/* Out of View frustrum rejection */
	MATH_AddScaleVector(&ProjectedCenter , pst_CamPos , pst_CamDir , - SphereRadius * CamOoSin );
	MATH_SubVector(&pCS , pst_SphereCenter , &ProjectedCenter );
	MATH_NormalizeVector(&pCS , &pCS );
	if (MATH_f_DotProduct(&pCS,pst_CamDir) < CamCos) return 0.0f;

	return Lod;
}
