FLOAT fGrowingSpeed1 = 0.0f;
FLOAT fGrowingSpeed2 = 0.0f;
FLOAT fGrowingFriction = 0.0f;
FLOAT fPhase1Duration = 0.2f;
FLOAT fPhase2Duration = 0.5f;
FLOAT fSizeMin = 0.02f;
FLOAT fSizeMax = 0.04f;
FLOAT fGravity = -6.0f;
FLOAT fGenerationRate = 0.001f;
FLOAT fSpeedMin = 1.0f;
FLOAT fSpeedMax = 3.0f;
FLOAT fTimeFactor = 0.2f;

int iPoolSize = 40;
int iSubMatNum = 0;
int iPhase1Color = 0xffffffff;
int iPhase2Color = 0xffffffff;
int iPhase3Color = 0xffffffff;
int iSpritesToGenerate = 64;

BOOL INT_RaySphere(MATH_tdst_Vector *_pst_Origin, MATH_tdst_Vector *_pst_Direction, INT_tdst_Sphere *_pst_Sphere);

void SPG2_GenerateLeafExplosion(OBJ_tdst_GameObject *pst_GO, MATH_tdst_Vector *_pos, FLOAT fRadius)
{   
    static BOOL bOff = TRUE;
    if(bOff) return;

    if (!SPG2_ulNumberOfLines)
        return;

    ULONG SphereCounter;
    ULONG LineCounter;
    MATH_tdst_Vector	LocalPos;

    INT_tdst_Sphere SPG2Sphere;
    SPG2Sphere.f_Radius = fRadius;//_pst_SPG2 -> SphereCollideRadius;
    if(SPG2Sphere.f_Radius<0.1f) 
        SPG2Sphere.f_Radius = 0.1f;
    if(SPG2Sphere.f_Radius>2.0f) 
        SPG2Sphere.f_Radius = 2.0f;
    SPG2Sphere.st_Center = *_pos;
    SPG2Sphere.st_Center.z += SPG2Sphere.f_Radius;
    WOR_tdst_World *pst_World = WOR_World_GetWorldOfObject(pst_GO);
    MATH_tdst_Vector stLocal, stZero, vPos;
    MATH_tdst_Matrix stInvWorld;

    BOOL bInt;

    LineCounter = SPG2_ulNumberOfLines;
    while (LineCounter --)                                    
    {
        bInt = INT_RaySphere(&SPG2_LinePos[LineCounter], &SPG2_LineDir[LineCounter], &SPG2Sphere);
        if (bInt)
        {
            vPos = *_pos;

            MATH_InitVector(&stZero ,0.0f , 0.0f , 0.0f);
            USHORT ti_GFX_Blam = GFX_i_Add(&pst_World->pst_GFX , 13 , NULL);

            GFX_FlagSet(pst_World->pst_GFX, ti_GFX_Blam, 0 , 1);
            GFX_FlagSet(pst_World->pst_GFX, ti_GFX_Blam, 2 , 1);

            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13000, fGrowingSpeed1     );    
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13001, fGrowingSpeed2     );    
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13002, fGrowingFriction   );  
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13003, fPhase1Duration    );   
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13004, fPhase2Duration    );   
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13005, fSizeMin           );          
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13006, fSizeMax           );          
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13007, fGravity           );          
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13008, fGenerationRate    );   
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13009, fSpeedMin          );         
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13010, fSpeedMax          );         
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13011, vPos.z             );           
            GFX_Setf(pst_World->pst_GFX, ti_GFX_Blam, 13012, fTimeFactor        );       

            GFX_Seti(pst_World->pst_GFX, ti_GFX_Blam, 13100, iPoolSize          );          
            GFX_Seti(pst_World->pst_GFX, ti_GFX_Blam, 13101, iSubMatNum         );         
            GFX_Seti(pst_World->pst_GFX, ti_GFX_Blam, 13103, iPhase1Color       );       
            GFX_Seti(pst_World->pst_GFX, ti_GFX_Blam, 13104, iPhase2Color       );       
            GFX_Seti(pst_World->pst_GFX, ti_GFX_Blam, 13105, iPhase3Color       );       
            GFX_Seti(pst_World->pst_GFX, ti_GFX_Blam, 13106, iSpritesToGenerate ); 
            GFX_Seti(pst_World->pst_GFX, ti_GFX_Blam, 13107, 0                  );                  

            GFX_Setv(pst_World->pst_GFX, ti_GFX_Blam, 13200, &vPos);            /* creation pos             */
            MATH_InitVector(&stLocal ,0.376f , 0.376f , 0.376f);
            GFX_Setv(pst_World->pst_GFX, ti_GFX_Blam, 13201, &stLocal);          /* speed direction min      */
            MATH_InitVector(&stLocal ,-0.376f , -0.376f , 0.0f);
            GFX_Setv(pst_World->pst_GFX, ti_GFX_Blam, 13202, &stLocal);          /* speed direction max      */
            MATH_InitVector(&stLocal ,0.02f , 0.02f , 0.02f);
            GFX_Setv(pst_World->pst_GFX, ti_GFX_Blam, 13203, &stLocal);         /* friction                 */
            GFX_Setv(pst_World->pst_GFX, ti_GFX_Blam, 13204, &stZero);          /* wind                     */
            GFX_Setv(pst_World->pst_GFX, ti_GFX_Blam, 13205, &stZero);          /* main position speed      */
            GFX_Setv(pst_World->pst_GFX, ti_GFX_Blam, 13206, &stZero);          /* main position friction   */

            GFX_SetMaterial(pst_World->pst_GFX, ti_GFX_Blam, (void *)pst_GO->pst_Base->pst_Visu->pst_Material);
        }
    }
}
