BOOL g_bForceApply = FALSE;
void SPG2_Modifier_ApplyForInstances(SPG2_tdst_Modifier *pst_SPG2)
{
    GEO_tdst_Object* pst_Obj = (GEO_tdst_Object*)OBJ_p_GetCurrentGeo(pst_SPG2->pParentModifier->pst_GO);
    if(!pst_Obj)
        return;

    if(!(pst_SPG2->pParentModifier->ul_Flags & MDF_C_Modifier_Inactive))
    {
        g_bForceApply = TRUE;
        SPG2_Modifier_Unapply(pst_SPG2->pParentModifier, pst_Obj);    
        g_bForceApply = FALSE;
    }
}

