ULONG SPG2_ul_Modifier_Load(MDF_tdst_Modifier *_pst_Mod, char *_pc_Buffer)
{
	/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
	SPG2_tdst_Modifier				*_pst_SPG2 ;
	unsigned char					*pc_Cur;
	/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
	SPG2_InitCachedData();


	pc_Cur = (unsigned char* )_pc_Buffer;
	_pst_SPG2  = (SPG2_tdst_Modifier *) _pst_Mod->p_Data;
	/* Skip size */
#if defined(XML_CONV_TOOL)
	gGaoSpg2SizeHack[gGaoSpg2SizeHackWPos++] = LOA_ReadLong((CHAR**)&pc_Cur);
#else
	LOA_ReadLong((char **)&pc_Cur);
#endif
	SPG2_Modifier_SaveLoad_Parrams(_pst_SPG2 , 2, (char **)&pc_Cur);

	st_CurrentGridMapper.SPG2_GRID_MAKS |= 1 << (_pst_SPG2->GridFilter & 0xf);
	st_CurrentGridMapper.GRID_NOISES[_pst_SPG2->GridFilter & 0xf] = _pst_SPG2->GridNoise;
	if (_pst_SPG2->ulFlags & SPG2_BurnedMode) 
		st_CurrentGridMapper.SPG2_GRID_MAKS |= 0x10000 << (_pst_SPG2->GridFilter & 0xf);
	
	LoadingHasBeenDetected = 10;


	if (_pst_Mod->pst_GO->ul_MyKey == 0x1E000B05)
	{
		/* Fire on lance detected. */
		_pst_SPG2->f_Density = fMin(1200.0f,_pst_SPG2->f_Density * 2.0f);
	}
	
#ifdef JADEFUSION
    // if no holder, use current gameobject as holder
    /*if(!_pst_SPG2->pSPG2Holder)
        _pst_SPG2->pSPG2Holder = _pst_Mod->pst_GO;
    */
    _pst_SPG2->pParentModifier = _pst_Mod;

#endif

	//return pc_Cur - _pc_Buffer ;
	return (ULONG)(pc_Cur - (unsigned char*)_pc_Buffer);
}

